import { Box, useMediaQuery } from "@mui/material";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import Navbar from "scenes/navbar.jsx";
import FriendListWidget from "scenes/widgets/FriendListWidget.jsx";
import MyPostWidget from "scenes/widgets/MyPostWidget.jsx";
import PostWidget from "scenes/widgets/PostWidget.jsx";
import UserWidget from "scenes/widgets/UserWidget.jsx";

const ProfilePage = () => {
    const [user, setUser] = useState(null);
    const { userId } = useParams();
    const token = useSelector((state) => state.token);
    const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");

    const getUser = async () => {
        const response = await fetch (`http://localhost:3001/users/${userId}`, {
            method: "GET",
            headers: { Authorization: `Bearer ${token}`}
        });
        const data = await response.json();
        setUser(data);
    };

    useEffect(() => {
        getUser();
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    if (!user) return null;

    return (<div>profilepage</div>);
};

export default ProfilePage;
